import React, { useState, useEffect } from 'react';
import Introduction from './components/Introduction';
import ServicePage from './pages/ServicePage';

function App() {
  const [selectedService, setSelectedService] = useState('Java');
  const [navbarScrolled, setNavbarScrolled] = useState(false);

  const handleServiceChange = (service) => {
    setSelectedService(service);
  };

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setNavbarScrolled(true);
      } else {
        setNavbarScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="App">
      <nav
        className={`fixed top-0 w-full p-4 flex items-center transition-all duration-500 ${
          navbarScrolled ? 'bg-blue-700 shadow-lg' : 'bg-blue-500'
        }`}
      >
        <div className="flex-shrink-0">
          <img
            src="src/assets/images/logo.png"
            alt="Logo"
            className="h-8 w-8 mr-4 transition-transform duration-300 transform hover:scale-110"
          />
        </div>

        {/* Navigation Links */}
        <ul className="flex justify-around w-full">
          <li>
            <a
              href="#introduction"
              className="text-white transition-colors duration-300 hover:text-yellow-300"
            >
              Introduction
            </a>
          </li>
          <li>
            <a
              href="#services"
              className="text-white transition-colors duration-300 hover:text-yellow-300"
            >
              Services
            </a>
          </li>
          <li>
            <a
              href="#HighlitedProduct"
              className="text-white transition-colors duration-300 hover:text-yellow-300"
            >
              Products
            </a>
          </li>
          <li>
            <a
              href="#testimonials"
              className="text-white transition-colors duration-300 hover:text-yellow-300"
            >
              Testimonials
            </a>
          </li>
          <li>
            <a
              href="#contact"
              className="text-white transition-colors duration-300 hover:text-yellow-300"
            >
              Contact
            </a>
          </li>
        </ul>
      </nav>

      <section id="introduction" className="p-4 pt-20">
        <Introduction />
      </section>

      <section id="services" className="p-4">
        <div className="flex justify-center mb-4">
          <button
            onClick={() => handleServiceChange('Java')}
            className={`p-2 mx-2 transition-all duration-300 transform ${
              selectedService === 'Java'
                ? 'bg-blue-600 text-white scale-105'
                : 'bg-gray-200 text-gray-800 hover:scale-105'
            }`}
          >
            Java
          </button>
          <button
            onClick={() => handleServiceChange('DBMS')}
            className={`p-2 mx-2 transition-all duration-300 transform ${
              selectedService === 'DBMS'
                ? 'bg-blue-600 text-white scale-105'
                : 'bg-gray-200 text-gray-800 hover:scale-105'
            }`}
          >
            DBMS
          </button>
          <button
            onClick={() => handleServiceChange('DS')}
            className={`p-2 mx-2 transition-all duration-300 transform ${
              selectedService === 'DS'
                ? 'bg-blue-600 text-white scale-105'
                : 'bg-gray-200 text-gray-800 hover:scale-105'
            }`}
          >
            Data Science
          </button>
        </div>
        <ServicePage selectedService={selectedService} />
      </section>
    </div>
  );
}

export default App;
