import React from 'react';


function ServiceCard({ name, description, image }) {
  const history = useHistory();

  const handleCardClick = () => {
    history.push(`/roadmap/${name}`);
  };

  return (
    <div 
      onClick={handleCardClick} 
      className="service-card border rounded-lg overflow-hidden shadow-lg p-4 cursor-pointer"
    >
      <img src={image} alt={name} className="w-full h-48 object-cover mb-4" />
      <h3 className="text-xl font-bold mb-2">{name}</h3>
      <p className="text-gray-700">{description.substring(0, 100)}...</p>
    </div>
  );
}

export default ServiceCard;
