import React from 'react';
import services from '../assets/images/services.png'; // Replace with the correct path to your image

function Introduction() {
  return (
    <section className="introduction p-4 flex flex-col md:flex-row items-center md:items-start">
      <div className="md:w-1/2 mb-4 md:mb-0">
        <h1 className="text-3xl font-bold mb-4">INTRODUCTION</h1>
        <p className="text-justify">
          GLOOM DEV is a dynamic startup specializing in Data Science, IoT, AI, and Machine Learning solutions. 
          Our institution is accredited by AICTE for excellence in technical education and recognized by MSME for its contributions to the growth of small and medium enterprises.
          Our mission is to empower businesses and individuals through innovative technologies and 
          comprehensive training programs. We focus on delivering bespoke solutions that foster digital 
          transformation and practical skill development. At GLOOM DEV, we are committed to making a significant 
          impact in the tech industry by providing customized and impactful solutions.
        </p>
      </div>
      <div className="md:w-1/2 flex justify-center">
        <img src={introImage} alt="Introduction" className="max-w-full h-auto rounded-lg shadow-lg" />
      </div>
    </section>
  );
}

export default Introduction;
