import React, { useRef } from 'react';
import javaImage from '../assets/images/java.jpeg';
import ds from '../assets/images/ds.jpeg';
import dbms from '../assets/images/dbms.png';
import cs from '../assets/images/cs.jpeg';
import mern from '../assets/images/mern.png';

function ServicePage() {
  const scrollRef = useRef(null);

  const scroll = (direction) => {
    if (direction === 'left') {
      scrollRef.current.scrollLeft -= 200;
    } else {
      scrollRef.current.scrollLeft += 200;
    }
  };

  const serviceCardStyle = {
    backgroundColor: 'white',
    borderRadius: '0.5rem',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    overflow: 'hidden',
    transform: 'scale(1)',
    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
  };

  const serviceCardHoverStyle = {
    transform: 'scale(1.05)',
    boxShadow: '0 8px 16px rgba(0, 0, 0, 0.2)',
  };

  const imageStyle = {
    width: '100%',
    height: '8rem',
    objectFit: 'cover',
  };

  const contentStyle = {
    padding: '1rem',
  };

  const titleStyle = {
    fontSize: '1.25rem',
    fontWeight: 'bold',
  };

  const descriptionStyle = {
    color: 'gray',
  };

  return (
    <div style={{ padding: '1rem' }}>
      <div style={{ display: 'flex', gap: '1rem', overflowX: 'auto' }}>
        <div
          style={serviceCardStyle}
          onMouseEnter={e => {
            e.currentTarget.style.transform = serviceCardHoverStyle.transform;
            e.currentTarget.style.boxShadow = serviceCardHoverStyle.boxShadow;
          }}
          onMouseLeave={e => {
            e.currentTarget.style.transform = 'scale(1)';
            e.currentTarget.style.boxShadow = '0 4px 8px rgba(0, 0, 0, 0.1)';
          }}
        >
          <img src={javaImage} alt="Java" style={imageStyle} />
          <div style={contentStyle}>
            <h3 style={titleStyle}>Java</h3>
            <p style={descriptionStyle}>A course on Java...</p>
          </div>
        </div>

        <div
          style={serviceCardStyle}
          onMouseEnter={e => {
            e.currentTarget.style.transform = serviceCardHoverStyle.transform;
            e.currentTarget.style.boxShadow = serviceCardHoverStyle.boxShadow;
          }}
          onMouseLeave={e => {
            e.currentTarget.style.transform = 'scale(1)';
            e.currentTarget.style.boxShadow = '0 4px 8px rgba(0, 0, 0, 0.1)';
          }}
        >
          <img src={ds} alt="Data Science" style={imageStyle} />
          <div style={contentStyle}>
            <h3 style={titleStyle}>Data Science</h3>
            <p style={descriptionStyle}>Data science combines math and statistics...</p>
          </div>
        </div>

        <div
          style={serviceCardStyle}
          onMouseEnter={e => {
            e.currentTarget.style.transform = serviceCardHoverStyle.transform;
            e.currentTarget.style.boxShadow = serviceCardHoverStyle.boxShadow;
          }}
          onMouseLeave={e => {
            e.currentTarget.style.transform = 'scale(1)';
            e.currentTarget.style.boxShadow = '0 4px 8px rgba(0, 0, 0, 0.1)';
          }}
        >
          <img src={dbms} alt="DBMS" style={imageStyle} />
          <div style={contentStyle}>
            <h3 style={titleStyle}>DBMS</h3>
            <p style={descriptionStyle}>
              Database Management System is a software or technology...
            </p>
          </div>
        </div>
      </div>

      <h2 className="text-3xl font-bold mt-8">Highlighted Products</h2>
      <div className="highlighted-products grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-4">
        <div
          style={serviceCardStyle}
          onMouseEnter={e => {
            e.currentTarget.style.transform = serviceCardHoverStyle.transform;
            e.currentTarget.style.boxShadow = serviceCardHoverStyle.boxShadow;
          }}
          onMouseLeave={e => {
            e.currentTarget.style.transform = 'scale(1)';
            e.currentTarget.style.boxShadow = '0 4px 8px rgba(0, 0, 0, 0.1)';
          }}
        >
          <img src={cs} alt="Cyber Security" style={imageStyle} />
