import React, { useState } from 'react';
import Introduction from './components/Introduction';
import ServicePage from './pages/ServicePage';

function App() {
  const [selectedService, setSelectedService] = useState('Java');

  const handleServiceChange = (service) => {
    setSelectedService(service);
  };

  return (
    <div className="App">
      <nav className="bg-blue-500 p-4 flex items-center">
        {/* Logo Section */}
        <div className="flex-shrink-0">
          <img src="src/assets/images/logo.png" alt="Logo" className="h-8 w-8 mr-4" />
        </div>

        {/* Navigation Links */}
        <ul className="flex justify-around w-full">
          <li><a href="#Introduction" className="text-white">Introduction</a></li>
          <li><a href="#ServiceCard" className="text-white">Services</a></li>
          <li><a href="#HighlitedProduct" className="text-white">Products</a></li>
          <li><a href="#ClientTestimonial" className="text-white">Testimonials</a></li>
          <li><a href="#Contact" className="text-white">Contact</a></li>
        </ul>
      </nav>

      <section id="introduction" className="p-4">
        <Introduction />
      </section>

      <section id="services" className="p-4">
        <div className="flex justify-center mb-4">
          <button
            onClick={() => handleServiceChange('Java')}
            className={`p-2 mx-2 ${selectedService === 'Java' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'}`}
          >
            Java
          </button>
          <button
            onClick={() => handleServiceChange('DBMS')}
            className={`p-2 mx-2 ${selectedService === 'DBMS' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'}`}
          >
            DBMS
          </button>
          <button
            onClick={() => handleServiceChange('DS')}
            className={`p-2 mx-2 ${selectedService === 'DS' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'}`}
          >
            Data Science
          </button>
        </div>
        <ServicePage selectedService={selectedService} />
      </section>
    </div>
  );
}

export default App;
